sourcesContent:
import mongoose from '@/database';
import Slugify from '../../utils/Slugify';

const ProjectSchema = new mongoose.Schema({
  Materia: {
    // Nome da matéria
    type: String,
    required: true,
    unique: true,
  },
  slug: {
    type: String,
    unique: true,
  },
  Professores: {
    // Professor que da a matéria
    type: [String],
    required: true,
  },
  Cursos: {
    // Cursos abrangidos que tem a matéria
    type: [String],
    required: true,
    lowercase: true,
  },
  featuredImage: {
    type: String,
    required: false,
  },
  images: [{ type: String }],

  createdAt: {
    type: Date,
    default: Date.now,
  },
});

ProjectSchema.pre('save', function (next) {
  const materia = this.Materia;
  this.slug = Slugify(materia);
  next();
});

export default mongoose.model('Project', ProjectSchema);

